mapper.select:
select member_no as memberNo, member_Id as memberId, member_name as memberName, member_phone as memberPhone, member_email as memberEmail, member_addr_main as memberAddrMain, member_addr_detail as memberAddrDetail, member_birth as memberBirth, member_enroll_date as memberEnrollDate, member_level as memberLevel from ( select rownum as rnum, a.* from ( select * from tbl_member
where member_name like '%' || #{keyword} || '%'
where member_id like'%' || #{keyword} || '%'
order by member_level
) a ) a where rnum between #{start} and #{end}

mapper.update:
update tbl_member
set member_level = #{memberLevel}
where member_id = #{memberId}

update tbl_org
set org_status = #{orgStatus}
where org_no = #{orgNo}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, a.* FROM ( SELECT b.biz_no AS bizNo, o.org_name AS orgName, c.donate_ctg AS bizCtg, b.biz_content AS bizContent, b.biz_donate_start AS bizDonateStart, b.biz_donate_end AS bizDonateEnd, b.biz_start AS bizStart, b.biz_end AS bizEnd, b.biz_goal AS bizGoal, b.biz_reg_date AS bizRegDate, b.biz_name AS bizName, b.biz_status AS bizStatus, b.biz_edit AS bizEdit, o.org_email AS orgEmail FROM tbl_donate_biz b JOIN tbl_org o ON b.org_no = o.org_no JOIN tbl_donate_code c ON b.donate_code = c.donate_code
b.biz_status = #{status}
AND b.biz_name LIKE '%' || #{keyword} || '%'
AND o.org_name LIKE '%' || #{keyword} || '%'
ORDER BY b.biz_status ASC, b.biz_reg_date DESC ) a ) WHERE rnum BETWEEN #{start} AND #{end}

select * FROM ( SELECT ROWNUM AS rnum, a.* FROM ( SELECT o.org_no as orgNo, o.org_id as orgId, o.org_name as orgName, o.org_biznum as orgBiznum, o.org_phone as orgPhone, o.org_email as orgEmail, o.org_addr_main as orgAddrMain, o.org_addr_detail as orgAddrDetail, o.org_introduce as orgIntroduce, o.org_account as orgAccount, o.org_account_bank as orgAccountBank, o.org_enroll_date as orgEnrollDate, o.org_temperature as orgTemperature, o.org_url as orgUrl, o.org_status as orgStatus from tbl_org o WHERE
o.org_status = 3
o.org_status = 4
o.org_status IN (3, 4)
) a ) a WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
update tbl_org
set org_status = #{orgStatus}
where org_no = #{orgNo}
