mapper.select:
SELECT count(*) FROM tbl_donate_biz WHERE biz_status = 1
AND donate_code IN
#{code}

SELECT biz_no AS bizNo, org_no AS orgNo, org_name AS orgName, donate_code AS donateCode, donate_ctg AS donateCtg, biz_name AS bizName, biz_content AS bizContent, biz_donate_start AS bizDonateStart, biz_donate_end AS bizDonateEnd, biz_start AS bizStart, biz_end AS bizEnd, biz_goal AS bizGoal, biz_status AS bizStatus, biz_reg_date AS bizRegDate, biz_edit AS bizEdit, biz_thumb AS bizThumbPath, donate_money AS donateMoney FROM ( SELECT rownum rnum, a.* FROM ( SELECT b.biz_no AS biz_no, b.org_no AS org_no, o.org_name AS org_name, b.donate_code AS donate_code, d.donate_ctg AS donate_ctg, b.biz_name AS biz_name, b.biz_content AS biz_content, TO_CHAR(b.biz_donate_start, 'YYYY-MM-DD') AS biz_donate_start, TO_CHAR(b.biz_donate_end, 'YYYY-MM-DD') AS biz_donate_end, TO_CHAR(b.biz_start, 'YYYY-MM-DD') AS biz_start, TO_CHAR(b.biz_end, 'YYYY-MM-DD') AS biz_end, b.biz_goal AS biz_goal, b.biz_status AS biz_status, TO_CHAR(b.biz_reg_date, 'YYYY-MM-DD') AS biz_reg_date, b.biz_edit AS biz_edit, b.biz_thumb AS biz_thumb, NVL(( SELECT SUM(d.donate_money) FROM tbl_donation_list d WHERE d.biz_no = b.biz_no ), 0) AS donate_money FROM tbl_donate_biz b JOIN tbl_org o ON b.org_no = o.org_no JOIN tbl_donate_code d ON b.donate_code = d.donate_code WHERE b.biz_status = 1 AND b.biz_end >= sysdate
AND b.donate_code IN
#{code}
ORDER BY b.biz_reg_date DESC ) a ) WHERE rnum BETWEEN #{start} AND #{end}

select sum(donate_money) AS donateMoney
	from tbl_donation_list
	where biz_no = #{parameter}

