mapper.select:
select count(*)
  from tbl_org
 where org_id = #{orgId}

select donate_code
  from tbl_org_donation
 where org_no = #{orgNo}

select org_pw as orgPw
  from tbl_org
 where org_no = #{orgNo}

mapper.update:
update tbl_org
   set org_pw = #{orgPw}
 where org_no = #{orgNo}

mapper.select:
select org_thumb as orgThumbPath
  from tbl_org
 where org_no = #{orgNo}

mapper.update:
update tbl_org
   set org_thumb = null
 where org_no = #{orgNo}

update tbl_org
   set org_thumb = #{orgThumbPath}
 where org_no = #{orgNo}

mapper.select:
select count(*) from tbl_donate_biz where org_no = #{orgNo}
and biz_status = 1 and biz_donate_end >= sysdate
and biz_status = 1 and sysdate > biz_donate_end

select biz_no as bizNo, donate_code as donateCode, biz_name as bizName, to_char(biz_donate_start-7, 'YYYY-MM-DD') as bizEnrollDate, to_char(biz_donate_start, 'YYYY-MM-DD') as bizDonateStart, to_char(biz_donate_end,'YYYY-MM-DD') as bizDonateEnd, to_char(biz_start, 'YYYY-MM-DD') as bizSatrt, to_char(biz_end, 'YYYY-MM-DD') as bizEnd, biz_goal as bizGoal, biz_status as bizStatus, a.rnum as rnum, a.payout_no as payoutYN from (select rownum rnum, a.* from (select d.*, p.payout_no from tbl_donate_biz d left join tbl_admin_payout_history p on d.biz_no = p.biz_no where org_no = #{orgNo}
and biz_status = 1 and to_char(biz_donate_end, 'YYYY-MM-DD') >= to_char(sysdate, 'YYYY-MM-DD')
and biz_status != 0 and to_char(sysdate, 'YYYY-MM-DD') > to_char(biz_donate_end, 'YYYY-MM-DD')
order by biz_donate_start desc) a ) a where rnum between #{start} and #{end}

select org_id as orgId
  from tbl_org
 where org_name = #{orgName}
   and org_phone = #{orgPhone}

select count(*)
  from tbl_org
 where org_id = #{orgId}
   and org_email = #{orgEmail}

mapper.update:
update tbl_org
   set org_pw = #{orgPw}
 where org_id = #{orgId}

update tbl_alarm
set alarm_read = 1
where alarm_no = #{_parameter}
